<?phpfunction realLogin($username, $hash) {	$test_session_auth = mysql_query("SELECT * FROM users WHERE(`username`='$username' AND `hash`='$hash')") 										or die(mysql_error());		if(countRows($test_session_auth) == 1) {		return true;	}		return false;	}function protect($str) {	return mysql_real_escape_string($str);}function getUserGroup($level) {	switch($level) {				case 0:			return "Member";		case 1: 			return "Donator";		case 2: 			return "Moderator";		case 3:			return "Administrator";		}	}function getUserHash($username, $password) {	$get_hash = mysql_query("SELECT * FROM users WHERE(`username`='$username' AND `password`='$password')") 							or die(mysql_error());		if(countRows($get_hash) == 1) {		while($row = mysql_fetch_array($get_hash)) {			$hash = $row['hash'];			return $hash;		}	} 	return "failed";}function encrypt($user, $pass) {	$pass = sha1($user . $pass . getEnc());	return $pass;}function getEnc() {	return "102021!#@&*002q#$%!%iJDWQIJQD()()K12348";}	function countRows($query) {				return mysql_num_rows($query);		}		function activateUser($hash) {		$update = mysql_query("UPDATE users SET `conf`=1 WHERE(`hash`='$hash')") or die(mysql_error());				if($update) {					return 1;				}				return 0;	}		function loginUser($username, $password) {		$password = encrypt($username,$password);		$auth = mysql_query("SELECT * FROM users WHERE(`username`='$username' AND `password`='$password')");				if($auth) {			if(countRows($auth) == 1) {								while($row = mysql_fetch_array($auth)) {									if($row['conf'] == 0) {						return "noa";					} else {						return "yes";										}				}							} else {					return "no";						}		} else {			$error = mysql_error();			return "failed: $error";		}		}?>