<?phpsession_start();require("../config/init.php");	$username = $_POST['username'];	$password = $_POST['password'];		if(!empty($username) and !empty($password)) {		$loginresult = loginUser($username,$password);				if($loginresult == "yes") {			$_SESSION['username'] = $username;			$_SESSION['id'] = getUserHash($username, encrypt($username,$password));			header("Location: ../index.php");		} else {			unset($_SESSION['username']);						if($loginresult == "no") {				$_SESSION['msg'] = "<div class='green'>Invalid username/password.</div>";			}  else if($loginresult == "noa") {				$_SESSION['msg'] = "<div class='green'>Your account has not be activated yet.</div>";			} else {				$_SESSION['msg'] = "<div class='green'>Login failed.</div>";			}						header("Location: ../login.php");		}			} else {		unset($_SESSION['username']);		$_SESSION['msg'] = "<div class='green'>Invalid username/password.</div>";		header("Location: ../login.php");	}?>